mapper.select:
SELECT r.name, r.issueDate, r.state, r.operateTime, d.name department, u.name operator
FROM regulation r, department d, user u

    r.departmentId=d.id AND r.operatorId=u.id

        AND r.name LIKE  concat('%', #{name}, '%')


        AND r.departmentId=#{departmentId}


        AND r.issueDate >= #{startDate}


        AND r.issueDate <= #{endDate}


        And r.state=#{state}


ORDER by r.issueDate DESC
